//获取兼容浏览器事件坐标的函数
function findCoordinates(e){
    //如果需要，用PageX/Y代替clientX/Y
    var x, y;
    if(e.changedTouches){    //touch事件
        x = e.changedTouches[0].clentX;
        y = e.changedTouches[0].clentY;
    }else{    //pointer或mouse事件
        x = e.clientX;
        y = e.clientY;
    }
    return [x, y];
}

