    }
    return newObj;
}

function deepCopy(obj){  //深拷贝

    if(typeof obj != 'object'){
        console.trace();
        return obj;
    }

    var newObj = {};

    for(var attr in obj){
        newObj[attr] = deepCopy(obj[attr]);
